import React, { useState, useEffect } from "react";
import { db } from "./firebase";
import { collection, addDoc, getDocs, query } from "firebase/firestore";
import vivaLogo from "./public/logo.png"; // Adjust path if needed

const HardwareDashboard = () => {
  const [devices, setDevices] = useState([]);
  const [showSuccess, setShowSuccess] = useState(false);

  const [form, setForm] = useState({
    serviceTag: "",
    deviceName: "",
    deviceType: "",
    brand: "",
    specification: "",
    assignedTo: "",
    employeeId: "",
    department: "",
    issueDate: "",
    returnDate: "",
    employeeEmail: "",
    notes: ""
  });

  // Removed fetchDevices from useEffect since we're not displaying devices
  useEffect(() => {}, []);

  const handleAddDevice = async () => {
    const { serviceTag, deviceName, assignedTo, employeeId, issueDate } = form;

    if (!serviceTag || !deviceName || !assignedTo || !employeeId || !issueDate) {
      alert("Please fill in all required fields: Service Tag, Device Name, Assigned To, Employee ID, and Issue Date.");
      return;
    }

    await addDoc(collection(db, "devices"), form);
    setForm({
      serviceTag: "",
      deviceName: "",
      deviceType: "",
      brand: "",
      specification: "",
      assignedTo: "",
      employeeId: "",
      department: "",
      issueDate: "",
      returnDate: "",
      employeeEmail: "",
      notes: ""
    });

    setShowSuccess(true); // Show success message (won't auto-hide)
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: value
    });
  };

  const dismissSuccess = () => {
    setShowSuccess(false); // Manually dismiss success message
  };

  return (
    <div className="App">
      <header className="app-header">
        <img src={vivaLogo} alt="VIVA Hardware Allocation" className="title-image" />
      </header>
      
      <div className="hardware-form">
        <h1>Hardware Allocation Form</h1>
        
        <form onSubmit={(e) => e.preventDefault()}>
          <div className="form-grid">
            {/* All your form fields remain unchanged */}
            {/* ... */}
          </div>
          <button 
            type="button" 
            onClick={handleAddDevice}
            className="submit-btn"
          >
            Add Device
          </button>
        </form>

        {/* Success message (stays until manually dismissed) */}
        {showSuccess && (
          <div className="success-message">
            <p>Device added successfully!</p>
            <button 
              onClick={dismissSuccess}
              className="dismiss-btn"
            >
              OK
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default HardwareDashboard;
