// Firebase + React Hardware Dashboard (Basic Version)

import React, { useEffect, useState } from "react";
import { initializeApp } from "firebase/app";
import { getFirestore, collection, getDocs, addDoc, query, where } from "firebase/firestore";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";

const firebaseConfig = {
  apiKey: "AIzaSyAqP5Lg1sy5NSiAVA48e11744jTRnaNZy0",
  authDomain: "assets-database-ede58.firebaseapp.com",
  projectId: "assets-database-ede58",
  storageBucket: "assets-database-ede58.firebasestorage.app",
  messagingSenderId: "983326285629",
  appId: "1:983326285629:web:88efe2924b2ecf762a2f54",
  measurementId: "G-NZ4N5TC97M"
};

const app = initializeApp(firebaseConfig);
const db = getFirestore(app);

export default function HardwareDashboard() {
  const [devices, setDevices] = useState([]);
  const [form, setForm] = useState({
    deviceId: "",
    deviceType: "",
    brand: "",
    assignedTo: "",
    employeeId: "",
    department: "",
    issueDate: "",
    returnDate: "",
    status: "",
    notes: ""
  });
  const [search, setSearch] = useState("");

  useEffect(() => {
    fetchDevices();
  }, []);

  const fetchDevices = async () => {
    const querySnapshot = await getDocs(collection(db, "devices"));
    const data = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    setDevices(data);
  };

  const handleInputChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleAddDevice = async () => {
    await addDoc(collection(db, "devices"), form);
    setForm({
      deviceId: "",
      deviceType: "",
      brand: "",
      assignedTo: "",
      employeeId: "",
      department: "",
      issueDate: "",
      returnDate: "",
      status: "",
      notes: ""
    });
    fetchDevices();
  };

  const filteredDevices = devices.filter(device =>
    device.assignedTo.toLowerCase().includes(search.toLowerCase()) ||
    device.employeeId.toLowerCase().includes(search.toLowerCase()) ||
    device.deviceId.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="p-4 max-w-6xl mx-auto">
      <h1 className="text-3xl font-bold mb-4">Hardware Issuance Dashboard</h1>

      <Card className="mb-6">
        <CardContent className="grid grid-cols-2 gap-4 p-4">
          {Object.keys(form).map((field) => (
            <Input
              key={field}
              name={field}
              placeholder={field}
              value={form[field]}
              onChange={handleInputChange}
            />
          ))}
          <Button className="col-span-2" onClick={handleAddDevice}>Add Device</Button>
        </CardContent>
      </Card>

      <Input
        placeholder="Search by name, ID, or device ID"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="mb-4"
      />

      <div className="grid gap-4">
        {filteredDevices.map(device => (
          <Card key={device.id}>
            <CardContent className="p-4">
              <div className="font-semibold">{device.deviceId} - {device.deviceType}</div>
              <div>Assigned to: {device.assignedTo} ({device.employeeId})</div>
              <div>Status: {device.status} | Dept: {device.department}</div>
              <div>Issued: {device.issueDate} | Return: {device.returnDate}</div>
              <div>Notes: {device.notes}</div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
}
