import React, { useState } from "react";
import { db } from "./firebase";
import { collection, addDoc } from "firebase/firestore";
import './App.css'; // Add this at the top of HardwareDashboard.js
import { query, where, getDocs } from "firebase/firestore";

const HardwareDashboard = () => {
  const [showSuccess, setShowSuccess] = useState(false);
  const [isAddingDevice, setIsAddingDevice] = useState(false);
  const [searchEmployeeId, setSearchEmployeeId] = useState("");
const [searchServiceTag, setSearchServiceTag] = useState("");
const [searchResults, setSearchResults] = useState([]);


  const [form, setForm] = useState({
    serviceTag: "",
    deviceName: "",
    deviceType: "",
    brand: "",
    specification: "",
    assignedTo: "",
    employeeId: "",
    department: "",
    issueDate: "",
    returnDate: "",
    employeeEmail: "",
    notes: ""
  });

  const handleAddDevice = async () => {
    const { serviceTag, deviceName, assignedTo, employeeId, issueDate } = form;

    if (!serviceTag || !deviceName || !assignedTo || !employeeId || !issueDate) {
      alert("Please fill in all required fields: Service Tag, Device Name, Assigned To, Employee ID, and Issue Date.");
      return;
    }

    try {
      await addDoc(collection(db, "devices"), form);
      setForm({
        serviceTag: "",
        deviceName: "",
        deviceType: "",
        brand: "",
        specification: "",
        assignedTo: "",
        employeeId: "",
        department: "",
        issueDate: "",
        returnDate: "",
        employeeEmail: "",
        notes: ""
      });
      setShowSuccess(true);
    } catch (error) {
      console.error("Error adding document: ", error);
      alert("There was an error submitting the form. Please try again.");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: value
    });
  };

   const dismissSuccess = () => {
    setShowSuccess(false);
    setIsAddingDevice(false); // Re-enable adding device
  };

  return (
    <div className="App" style={{
      backgroundImage: `url(${process.env.PUBLIC_URL + '/images/bgimg.jpg'})`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      backgroundAttachment: 'fixed',
      minHeight: '100vh',
      padding: '20px'
    }}>
      <header className="app-header">
        <img 
          src={process.env.PUBLIC_URL + '/images/viva.png'} 
          alt="VIVA Hardware Allocation" 
          className="title-image" 
        />
      </header>
      
      <div className="hardware-form">
        <h1>VIVA Hardware Allocation Form</h1>
        
        <form onSubmit={(e) => e.preventDefault()}>
          <div className="form-grid">
            <div className="form-group">
              <label className="required">Service Tag</label>
              <input
                type="text"
                name="serviceTag"
                value={form.serviceTag}
                onChange={handleChange}
                required
                disabled={showSuccess}
              />
            </div>
            
            <div className="form-group">
              <label className="required">Device Name</label>
              <input
                type="text"
                name="deviceName"
                value={form.deviceName}
                onChange={handleChange}
                required
                disabled={showSuccess}
              />
            </div>
            
            <div className="form-group">
              <label>Device Type</label>
              <input
                type="text"
                name="deviceType"
                value={form.deviceType}
                onChange={handleChange}
                disabled={showSuccess}
              />
            </div>
            
            <div className="form-group">
              <label>Brand</label>
              <input
                type="text"
                name="brand"
                value={form.brand}
                onChange={handleChange}
                disabled={showSuccess}
              />
            </div>
            
            <div className="form-group">
              <label>Specification</label>
              <input
                type="text"
                name="specification"
                value={form.specification}
                onChange={handleChange}
                disabled={showSuccess}
              />
            </div>
            
            <div className="form-group">
              <label className="required">Assigned To (Employee Name)</label>
              <input
                type="text"
                name="assignedTo"
                value={form.assignedTo}
                onChange={handleChange}
                required
                disabled={showSuccess}
              />
            </div>
            
            <div className="form-group">
              <label className="required">Employee ID</label>
              <input
                type="text"
                name="employeeId"
                value={form.employeeId}
                onChange={handleChange}
                required
                disabled={showSuccess}
              />
            </div>
            
            <div className="form-group">
              <label>Department</label>
              <input
                type="text"
                name="department"
                value={form.department}
                onChange={handleChange}
                disabled={showSuccess}
              />
            </div>
            
            <div className="form-group">
              <label className="required">Issue Date</label>
              <input
                type="date"
                name="issueDate"
                value={form.issueDate}
                onChange={handleChange}
                required
                disabled={showSuccess}
              />
            </div>
            
            <div className="form-group">
              <label>Return Date</label>
              <input
                type="date"
                name="returnDate"
                value={form.returnDate}
                onChange={handleChange}
                disabled={showSuccess}
              />
            </div>
            
            <div className="form-group" style={{ gridColumn: "span 2" }}>
              <label>Employee Email Address</label>
              <input
                type="email"
                name="employeeEmail"
                value={form.employeeEmail}
                onChange={handleChange}
                disabled={showSuccess}
              />
            </div>
            
            <div className="form-group" style={{ gridColumn: "span 2" }}>
              <label>Notes</label>
              <textarea
                name="notes"
                value={form.notes}
                onChange={handleChange}
                disabled={showSuccess}
              />
            </div>
            
            <button 
              type="button" 
              onClick={handleAddDevice}
              className="submit-btn"
              disabled={showSuccess} // <-- disables Add Device button while success shown
            >
              Add Device
            </button>
          </div>
        </form>

        {showSuccess && (
          <div className="success-message">
            <p>Device added successfully!</p>
            <button 
              onClick={dismissSuccess}
              className="dismiss-btn"
            >
              OK
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default HardwareDashboard;
