import React, { useState, useEffect } from "react";
import { db } from "./firebase";
import { collection, addDoc, getDocs, query } from "firebase/firestore";
import titleImage from "./public/viva.png"; // Update this path

const HardwareDashboard = () => {
  const [devices, setDevices] = useState([]);
  const [showSuccess, setShowSuccess] = useState(false);
  const [showDevices, setShowDevices] = useState(true);

  const [form, setForm] = useState({
    serviceTag: "",
    deviceName: "",
    deviceType: "",
    brand: "",
    specification: "",
    assignedTo: "",
    employeeId: "",
    department: "",
    issueDate: "",
    returnDate: "",
    employeeEmail: "",
    notes: ""
  });

  useEffect(() => {
    fetchDevices();
  }, []);

  const fetchDevices = async () => {
    const q = query(collection(db, "devices"));
    const querySnapshot = await getDocs(q);
    const devicesArray = [];
    querySnapshot.forEach((doc) => {
      devicesArray.push(doc.data());
    });
    setDevices(devicesArray);
  };

  const handleAddDevice = async () => {
    const { serviceTag, deviceName, assignedTo, employeeId, issueDate } = form;

    if (!serviceTag || !deviceName || !assignedTo || !employeeId || !issueDate) {
      alert("Please fill in all required fields: Service Tag, Device Name, Assigned To, Employee ID, and Issue Date.");
      return;
    }

    await addDoc(collection(db, "devices"), form);
    setForm({
      serviceTag: "",
      deviceName: "",
      deviceType: "",
      brand: "",
      specification: "",
      assignedTo: "",
      employeeId: "",
      department: "",
      issueDate: "",
      returnDate: "",
      employeeEmail: "",
      notes: ""
    });

    setShowSuccess(true);
    setShowDevices(false);
    setTimeout(() => setShowSuccess(false), 3000);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: value
    });
  };

  return (
    <div className="App">
      <header className="app-header">
        <img src={titleImage} alt="VIVA Hardware Allocation" className="title-image" />
      </header>
      
      <div className="hardware-form">
        <h1>Hardware Allocation Form</h1>
        
        <form onSubmit={(e) => e.preventDefault()}>
          <div className="form-grid">
            <div className="form-group">
              <label className="required">Service Tag</label>
              <input
                type="text"
                name="serviceTag"
                value={form.serviceTag}
                onChange={handleChange}
              />
            </div>
            
            <div className="form-group">
              <label className="required">Device Name</label>
              <input
                type="text"
                name="deviceName"
                value={form.deviceName}
                onChange={handleChange}
              />
            </div>
            
            <div className="form-group">
              <label>Device Type</label>
              <input
                type="text"
                name="deviceType"
                value={form.deviceType}
                onChange={handleChange}
              />
            </div>
            
            <div className="form-group">
              <label>Brand</label>
              <input
                type="text"
                name="brand"
                value={form.brand}
                onChange={handleChange}
              />
            </div>
            
            <div className="form-group">
              <label>Specification</label>
              <input
                type="text"
                name="specification"
                value={form.specification}
                onChange={handleChange}
              />
            </div>
            
            <div className="form-group">
              <label className="required">Assigned To (Employee Name)</label>
              <input
                type="text"
                name="assignedTo"
                value={form.assignedTo}
                onChange={handleChange}
              />
            </div>
            
            <div className="form-group">
              <label className="required">Employee ID</label>
              <input
                type="text"
                name="employeeId"
                value={form.employeeId}
                onChange={handleChange}
              />
            </div>
            
            <div className="form-group">
              <label>Department</label>
              <input
                type="text"
                name="department"
                value={form.department}
                onChange={handleChange}
              />
            </div>
            
            <div className="form-group">
              <label className="required">Issue Date</label>
              <input
                type="date"
                name="issueDate"
                value={form.issueDate}
                onChange={handleChange}
              />
            </div>
            
            <div className="form-group">
              <label>Return Date</label>
              <input
                type="date"
                name="returnDate"
                value={form.returnDate}
                onChange={handleChange}
              />
            </div>
            
            <div className="form-group" style={{ gridColumn: "span 2" }}>
              <label>Employee Email Address</label>
              <input
                type="email"
                name="employeeEmail"
                value={form.employeeEmail}
                onChange={handleChange}
              />
            </div>
            
            <div className="form-group" style={{ gridColumn: "span 2" }}>
              <label>Notes</label>
              <textarea
                name="notes"
                value={form.notes}
                onChange={handleChange}
              />
            </div>
            
            <button 
              type="button" 
              onClick={handleAddDevice}
              className="submit-btn"
            >
              Add Device
            </button>
          </div>
        </form>

        {showSuccess && (
          <div className="success-message">
            Device added successfully!
          </div>
        )}
      </div>
    </div>
  );
};

export default HardwareDashboard;
