import React, { useState, useEffect } from "react";
import { db } from "./firebase"; // Ensure you have firebase configured
import { collection, addDoc, getDocs, query, where } from "firebase/firestore";

const HardwareDashboard = () => {
  const [devices, setDevices] = useState([]);
  const [form, setForm] = useState({
    deviceId: "",
    deviceType: "",
    brand: "",
    assignedTo: "",
    employeeId: "",
    department: "",
    issueDate: "",
    returnDate: "",
    status: "",
    notes: ""
  });

  useEffect(() => {
    fetchDevices();
  }, []);

  const fetchDevices = async () => {
    const q = query(collection(db, "devices"));
    const querySnapshot = await getDocs(q);
    const devicesArray = [];
    querySnapshot.forEach((doc) => {
      devicesArray.push(doc.data());
    });
    setDevices(devicesArray);
  };

  const handleAddDevice = async () => {
    await addDoc(collection(db, "devices"), form);
    setForm({
      deviceId: "",
      deviceType: "",
      brand: "",
      assignedTo: "",
      employeeId: "",
      department: "",
      issueDate: "",
      returnDate: "",
      status: "",
      notes: ""
    });
    fetchDevices();
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: value
    });
  };

  return (
    <div>
      <h1>Hardware Dashboard</h1>
      <form onSubmit={(e) => e.preventDefault()}>
        <div>
          <label>Device ID</label>
          <input
            type="text"
            name="deviceId"
            value={form.deviceId}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Device Type</label>
          <input
            type="text"
            name="deviceType"
            value={form.deviceType}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Brand</label>
          <input
            type="text"
            name="brand"
            value={form.brand}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Assigned To</label>
          <input
            type="text"
            name="assignedTo"
            value={form.assignedTo}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Employee ID</label>
          <input
            type="text"
            name="employeeId"
            value={form.employeeId}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Department</label>
          <input
            type="text"
            name="department"
            value={form.department}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Issue Date</label>
          <input
            type="date"
            name="issueDate"
            value={form.issueDate}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Return Date</label>
          <input
            type="date"
            name="returnDate"
            value={form.returnDate}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Status</label>
          <input
            type="text"
            name="status"
            value={form.status}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Notes</label>
          <textarea
            name="notes"
            value={form.notes}
            onChange={handleChange}
          />
        </div>
        <button type="button" onClick={handleAddDevice}>
          Add Device
        </button>
      </form>

      <h2>Devices</h2>
      <ul>
        {devices.map((device, index) => (
          <li key={index}>
            <strong>{device.deviceId}</strong> - {device.deviceType} -{" "}
            {device.brand}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default HardwareDashboard;
